  }

  /* Specific overrides for common text elements */
  h1, h2, h3, h4, h5, h6, p, span, div, a, li, td, th, label, input::placeholder {
    color: white !important;
  }

  /* Override any remaining color classes */
  .text-muted-foreground,
  .text-gray-600,
  .text-gray-500,
  .text-slate-600,
  .text-neutral-600 {
    color: white !important;
  }

  /* Restore proper border-radius for UI elements that need it */
  .rounded-lg {
    border-radius: 0.5rem !important;
  }

  .rounded-xl {
    border-radius: 0.75rem !important;
  }

  .rounded-2xl {
    border-radius: 1rem !important;
  }

  /* Accessible focus styles for blue theme */
  *:focus-visible {
    outline: 2px solid hsl(var(--ring));
    outline-offset: 2px;
  }

  /* From Uiverse.io by mrhyddenn - Updated for #180806 theme */
  .shadow__btn {
    padding: 10px 20px;
    border: none;
    font-size: 17px;
    color: hsl(var(--foreground));
    border-radius: 7px;
    letter-spacing: 4px;
    font-weight: 700;
    text-transform: uppercase;
    transition: 0.5s;
    transition-property: box-shadow;
    background: hsl(var(--gold));
    box-shadow: 0 0 25px hsl(var(--gold));
  }

  .shadow__btn:hover {
    box-shadow: 0 0 5px hsl(var(--gold)),
                0 0 25px hsl(var(--gold)),
                0 0 50px hsl(var(--gold)),
                0 0 100px hsl(var(--gold));
  }

  /* Image Loop Animation Styles */
  .image-loop-section {
    position: relative;
  }

  .image-loop-container {
    width: 100%;
    overflow: hidden;
    mask: linear-gradient(90deg, transparent, white 20%, white 80%, transparent);
    -webkit-mask: linear-gradient(90deg, transparent, white 20%, white 80%, transparent);
  }

  .image-loop-track {
    display: flex;
    animation: imageLoop 60s linear infinite;
    width: fit-content;
  }

  .image-loop-track-static {
    display: flex;
    width: fit-content;
  }

  .image-loop-item {
    flex-shrink: 0;
    margin-right: 2rem;
    width: 300px;
    height: 200px;
    overflow: hidden;
    border-radius: 12px;
    box-shadow: 0 10px 30px rgba(1, 50, 32, 0.5);
  }

  .image-loop-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transition: transform 0.3s ease;
  }

  .image-loop-item:hover .image-loop-img {
    transform: scale(1.05);
  }

  @keyframes imageLoop {
    0% {
      transform: translateX(0);
    }
    100% {
      transform: translateX(-50%);
    }
  }

  /* Pause animation on hover for better UX */
  .image-loop-track:hover {
    animation-play-state: paused;
  }

  /* Alternating Section Backgrounds */
  .section-pastel-blue {
    @apply bg-background text-foreground;
  }

  .section-hard-blue {
    @apply bg-primary text-primary-foreground;
  }

  /* Mobile responsive adjustments */
  @media (max-width: 768px) {
    .image-loop-item {
      width: 250px;
      height: 167px;
      margin-right: 1rem;
    }

    .image-loop-track {
      animation-duration: 45s;
    }
  }
}

/* UIverse Location Guide Button - Updated for #180806 theme */
.location-guide-btn {
  width: 10em;
  position: relative;
  height: 3.5em;
  border: 3px ridge hsl(var(--gold));
  outline: none;
  background-color: transparent;
  color: hsl(var(--foreground));
  transition: 1s;
  border-radius: 0.3em;
  font-size: 16px;
  font-weight: bold;
  cursor: pointer;
  z-index: 1;
  display: flex;
  align-items: center;
  justify-content: center;
}

.location-guide-btn::after {
  content: "";
  position: absolute;
  top: -10px;
  left: 3%;
  width: 95%;
  height: 40%;
  background-color: hsl(var(--primary));
  transition: 0.5s;
  transform-origin: center;
  z-index: -1;
}

.location-guide-btn::before {
  content: "";
  transform-origin: center;
  position: absolute;
  top: 80%;
  left: 3%;
  width: 95%;
  height: 40%;
  background-color: hsl(var(--primary));
  transition: 0.5s;
  z-index: -1;
}

.location-guide-btn:hover::before, 
.location-guide-btn:hover::after {
  transform: scale(0);
}

.location-guide-btn:hover {
  box-shadow: inset 0px 0px 25px hsl(var(--gold));
}

