import { useRef, useEffect } from 'react';

interface DotGridProps {
  baseColor?: string;
  activeColor?: string;
  dotSize?: number;
  gap?: number;
  proximity?: number;
  shockRadius?: number;
  shockStrength?: number;
  resistance?: number;
  returnDuration?: number;
}

const DotGrid = ({
  baseColor = '#333333',
  activeColor = '#6366f1',
  dotSize = 5,
  gap = 15,
  proximity = 120,
  shockRadius = 250,
  shockStrength = 5,
  resistance = 750,
  returnDuration = 1.5
}: DotGridProps) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationIdRef = useRef<number>();
  const dotsRef = useRef<Array<{
    x: number;
    y: number;
    originalX: number;
    originalY: number;
    vx: number;
    vy: number;
    active: boolean;
    opacity: number;
  }>>([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      initializeDots();
    };

    const initializeDots = () => {
      dotsRef.current = [];
      const cols = Math.ceil(canvas.width / gap);
      const rows = Math.ceil(canvas.height / gap);

      for (let i = 0; i <= cols; i++) {
        for (let j = 0; j <= rows; j++) {
          const x = i * gap;
          const y = j * gap;
          dotsRef.current.push({
            x,
            y,
            originalX: x,
            originalY: y,
            vx: 0,
            vy: 0,
            active: false,
            opacity: 0.3
          });
        }
      }
    };

    const handleMouseMove = (event: MouseEvent) => {
      const mouseX = event.clientX;
      const mouseY = event.clientY;

      dotsRef.current.forEach(dot => {
        const dx = mouseX - dot.x;
        const dy = mouseY - dot.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < proximity) {
          dot.active = true;
          dot.opacity = Math.min(1, 1 - distance / proximity);

          if (distance < shockRadius) {
            const force = (shockRadius - distance) / shockRadius * shockStrength;
            const angle = Math.atan2(dy, dx);
            dot.vx -= Math.cos(angle) * force;
            dot.vy -= Math.sin(angle) * force;
          }
        } else {
          dot.active = false;
          dot.opacity = 0.3;
        }
      });
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      dotsRef.current.forEach(dot => {
        // Apply physics
        const returnForceX = (dot.originalX - dot.x) / resistance;
        const returnForceY = (dot.originalY - dot.y) / resistance;

        dot.vx += returnForceX;
        dot.vy += returnForceY;
        dot.vx *= (1 - 1 / returnDuration / 60); // Damping
        dot.vy *= (1 - 1 / returnDuration / 60);

        dot.x += dot.vx;
        dot.y += dot.vy;

        // Draw dot
        ctx.beginPath();
        ctx.arc(dot.x, dot.y, dotSize / 2, 0, Math.PI * 2);

        if (dot.active) {
          // Parse hex color and apply opacity
          const r = parseInt(activeColor.slice(1, 3), 16);
          const g = parseInt(activeColor.slice(3, 5), 16);
          const b = parseInt(activeColor.slice(5, 7), 16);
          ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${dot.opacity})`;
        } else {
          const r = parseInt(baseColor.slice(1, 3), 16);
          const g = parseInt(baseColor.slice(3, 5), 16);
          const b = parseInt(baseColor.slice(5, 7), 16);
          ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${dot.opacity})`;
        }

        ctx.fill();
      });

      animationIdRef.current = requestAnimationFrame(animate);
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    window.addEventListener('mousemove', handleMouseMove);
    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      window.removeEventListener('mousemove', handleMouseMove);
      if (animationIdRef.current) {
        cancelAnimationFrame(animationIdRef.current);
      }
    };
  }, [baseColor, activeColor, dotSize, gap, proximity, shockRadius, shockStrength, resistance, returnDuration]);

  return (
    <canvas
      ref={canvasRef}
      className="absolute inset-0 pointer-events-none z-0"
      data-testid="dot-grid-background"
    />
  );
};

export default DotGrid;

